// 언어별 이메일 템플릿 선택 및 데이터 준비
const data = items[0].json;
const properties = data.properties;

// 기본 정보 추출
const bookingNumber = properties['예약번호'].title[0].plain_text;
const dateTime = new Date(properties['예약일시'].date.start);
const branch = properties['지점'].select.name;
const spaceType = properties['공간유형'].select.name;
const roomNumber = properties['룸번호']?.select?.name;
const floor = properties['층']?.select?.name;
const guests = properties['인원'].number;
const name = properties['이름'].rich_text[0].plain_text;
const email = properties['이메일'].email;
const language = properties['언어'].select.name;
const specialRequest = properties['특별요청']?.rich_text[0]?.plain_text || '';

// 언어별 설정
const langCode = language === 'Korean' ? 'ko' : language === 'Thai' ? 'th' : 'en';

// 날짜 포맷팅
const dateFormatted = {
  ko: dateTime.toLocaleDateString('ko-KR', {
    timeZone: 'Asia/Bangkok',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long'
  }),
  th: dateTime.toLocaleDateString('th-TH', {
    timeZone: 'Asia/Bangkok',
    year: 'numeric',
    month: 'long', 
    day: 'numeric',
    weekday: 'long'
  }),
  en: dateTime.toLocaleDateString('en-US', {
    timeZone: 'Asia/Bangkok',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long'
  })
};

const timeFormatted = dateTime.toLocaleTimeString('en-US', {
  timeZone: 'Asia/Bangkok',
  hour: '2-digit',
  minute: '2-digit',
  hour12: false
});

// 지점 정보
const branchInfo = {
  'Ekamai': {
    ko: {
      name: '에까마이 지점',
      address: '에카마이 BTS 4번 출구, 수쿰윗 병원 맞은편',
      phone: '095-868-6826'
    },
    th: {
      name: 'สาขาเอกมัย',
      address: 'BTS เอกมัย ทางออก 4 ตรงข้ามโรงพยาบาลสุขุมวิท',
      phone: '095-868-6826'
    },
    en: {
      name: 'Ekamai Branch',
      address: 'BTS Ekamai Exit 4, opposite Sukhumvit Hospital',
      phone: '095-868-6826'
    }
  },
  'Phrom Phong': {
    ko: {
      name: '프롬퐁 지점',
      address: '수쿰윗 소이 24, 아리스톤 호텔 1층',
      phone: '02-115-5500'
    },
    th: {
      name: 'สาขาพร้อมพงษ์',
      address: 'สุขุมวิท ซอย 24 ชั้น 1 โรงแรมอริสตัน',
      phone: '02-115-5500'
    },
    en: {
      name: 'Phrom Phong Branch',
      address: 'Sukhumvit Soi 24, 1st Floor Ariston Hotel',
      phone: '02-115-5500'
    }
  }
};

// 공간 정보
const spaceDetail = spaceType === '룸' ? roomNumber : floor;
const spaceInfo = {
  ko: spaceType === '룸' ? `룸 ${roomNumber}` : `홀 ${floor}`,
  th: spaceType === '룸' ? `ห้อง${roomNumber.replace('번룸', '')}` : `ชั้น${floor.replace('층', '')}`,
  en: spaceType === '룸' ? `Room ${roomNumber.replace('번룸', '')}` : `${floor.replace('층', '')} Floor`
};

// 구글맵 링크
const googleMapsLinks = {
  'Ekamai': 'https://maps.google.com/?q=Ekamai+BTS+Station+Exit+4',
  'Phrom Phong': 'https://maps.google.com/?q=Sukhumvit+Soi+24+Ariston+Hotel'
};

return [{
  json: {
    bookingNumber,
    name,
    email,
    language: langCode,
    dateFormatted: dateFormatted[langCode],
    timeFormatted,
    branch,
    branchInfo: branchInfo[branch][langCode],
    spaceInfo: spaceInfo[langCode],
    guests,
    specialRequest,
    googleMapsLink: googleMapsLinks[branch]
  }
}];

// 언어별 이메일 템플릿 생성
const data = items[0].json;

const templates = {
  ko: {
    subject: `[북막골] 예약이 확정되었습니다 - ${data.bookingNumber}`,
    body: `안녕하세요, ${data.name}님!

북막골 ${data.branchInfo.name} 예약이 확정되었습니다.

━━━━━━━━━━━━━━━━━━━━━
📋 예약 정보
━━━━━━━━━━━━━━━━━━━━━

예약번호: ${data.bookingNumber}
날짜: ${data.dateFormatted}
시간: ${data.timeFormatted}
지점: ${data.branchInfo.name}
공간: ${data.spaceInfo}
인원: ${data.guests}명

━━━━━━━━━━━━━━━━━━━━━
📍 오시는 길
━━━━━━━━━━━━━━━━━━━━━

${data.branchInfo.address}
전화: ${data.branchInfo.phone}

🗺️ 길찾기: ${data.googleMapsLink}

━━━━━━━━━━━━━━━━━━━━━
⚠️ 예약 안내
━━━━━━━━━━━━━━━━━━━━━

• 예약 취소/변경: 전화 또는 라인으로 연락주세요
• 룸 예약: 평균 이용시간 3-4시간
• 영업시간: 11:00-15:00, 16:00-22:00
${data.specialRequest ? `• 특별요청: ${data.specialRequest}` : ''}

맛있는 식사 준비하고 기다리겠습니다!

감사합니다.
북막골 드림`
  },
  th: {
    subject: `[Bookmakgol] การจองของคุณได้รับการยืนยันแล้ว - ${data.bookingNumber}`,
    body: `สวัสดีค่ะ/ครับ คุณ${data.name}!

การจองของคุณที่ Bookmakgol ${data.branchInfo.name} ได้รับการยืนยันแล้ว

━━━━━━━━━━━━━━━━━━━━━
📋 ข้อมูลการจอง
━━━━━━━━━━━━━━━━━━━━━

เลขที่การจอง: ${data.bookingNumber}
วันที่: ${data.dateFormatted}
เวลา: ${data.timeFormatted}
สาขา: ${data.branchInfo.name}
พื้นที่: ${data.spaceInfo}
จำนวน: ${data.guests} ท่าน

━━━━━━━━━━━━━━━━━━━━━
📍 ที่อยู่
━━━━━━━━━━━━━━━━━━━━━

${data.branchInfo.address}
โทร: ${data.branchInfo.phone}

🗺️ นำทาง: ${data.googleMapsLink}

━━━━━━━━━━━━━━━━━━━━━
⚠️ ข้อมูลการจอง
━━━━━━━━━━━━━━━━━━━━━

• ยกเลิก/เปลี่ยนแปลง: โทรหรือแจ้งทาง Line
• การจองห้อง: เวลาใช้งานโดยเฉลี่ย 3-4 ชั่วโมง
• เวลาเปิดให้บริการ: 11:00-15:00, 16:00-22:00
${data.specialRequest ? `• ความต้องการพิเศษ: ${data.specialRequest}` : ''}

เตรียมอาหารอร่อยรอคุณอยู่นะคะ/ครับ!

ขอบคุณค่ะ/ครับ
Bookmakgol`
  },
  en: {
    subject: `[Bookmakgol] Your Reservation is Confirmed - ${data.bookingNumber}`,
    body: `Hello ${data.name},

Your reservation at Bookmakgol ${data.branchInfo.name} has been confirmed.

━━━━━━━━━━━━━━━━━━━━━
📋 Booking Information
━━━━━━━━━━━━━━━━━━━━━

Booking Number: ${data.bookingNumber}
Date: ${data.dateFormatted}
Time: ${data.timeFormatted}
Branch: ${data.branchInfo.name}
Space: ${data.spaceInfo}
Party Size: ${data.guests} guests

━━━━━━━━━━━━━━━━━━━━━
📍 Location
━━━━━━━━━━━━━━━━━━━━━

${data.branchInfo.address}
Phone: ${data.branchInfo.phone}

🗺️ Directions: ${data.googleMapsLink}

━━━━━━━━━━━━━━━━━━━━━
⚠️ Important Information
━━━━━━━━━━━━━━━━━━━━━

• Cancel/Modify: Please call or message via Line
• Private Room: Average duration 3-4 hours
• Operating Hours: 11:00-15:00, 16:00-22:00
${data.specialRequest ? `• Special Requests: ${data.specialRequest}` : ''}

We look forward to serving you!

Best regards,
Bookmakgol`
  }
};

const template = templates[data.language];

return [{
  json: {
    ...data,
    emailSubject: template.subject,
    emailBody: template.body
  }
}];
