// Calculate tomorrow's date
const today = new Date();
const tomorrow = new Date(today);
tomorrow.setDate(tomorrow.getDate() + 1);

// Convert to Bangkok timezone
const bangkokTime = new Intl.DateTimeFormat('en-CA', {
  timeZone: 'Asia/Bangkok',
  year: 'numeric',
  month: '2-digit',
  day: '2-digit'
}).format(tomorrow);

return [{
  json: {
    tomorrowDate: bangkokTime,
    searchStartDate: `${bangkokTime}T00:00:00.000+07:00`,
    searchEndDate: `${bangkokTime}T23:59:59.999+07:00`
  }
}];

