// URL 파라미터 파싱 및 액션 결정
const query = items[0].json.query || {};
const action = items[0].json.path.includes('/confirm') ? 'confirm' : 'cancel';
const bookingNumber = query.id;

if (!bookingNumber) {
  return [{
    json: {
      error: true,
      message: 'Booking ID is required'
    }
  }];
}

return [{
  json: {
    action: action,
    bookingNumber: bookingNumber,
    error: false
  }
}];

// 고객 정보 추출 및 이메일 데이터 준비
const reservationData = items[0].json[1];
const actionData = items[0].json[0];
const properties = reservationData.properties;

// 기본 정보 추출
const bookingNumber = properties['Booking Number'].title[0].plain_text;
const dateTime = new Date(properties['Reservation DateTime'].date.start);
const branch = properties['Branch'].select.name;
const spaceType = properties['Space Type'].select.name;
const roomNumber = properties['Room Number']?.select?.name;
const floor = properties['Floor']?.select?.name;
const guests = properties['Guests'].number;
const name = properties['Name'].rich_text[0].plain_text;
const email = properties['Email'].email;
const language = properties['Language'].select.name;
const specialRequest = properties['Special Request']?.rich_text[0]?.plain_text || '';

// 언어별 설정
const langCode = language === 'Korean' ? 'ko' : language === 'Thai' ? 'th' : 'en';

// 날짜 포맷팅
const dateFormatted = {
  ko: dateTime.toLocaleDateString('ko-KR', {
    timeZone: 'Asia/Bangkok',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long'
  }),
  th: dateTime.toLocaleDateString('th-TH', {
    timeZone: 'Asia/Bangkok',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long'
  }),
  en: dateTime.toLocaleDateString('en-US', {
    timeZone: 'Asia/Bangkok',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    weekday: 'long'
  })
};

const timeFormatted = dateTime.toLocaleTimeString('en-US', {
  timeZone: 'Asia/Bangkok',
  hour: '2-digit',
  minute: '2-digit',
  hour12: false
});

// 지점 정보
const branchInfo = {
  'Ekamai': {
    ko: { name: '에까마이 지점', address: '에카마이 BTS 4번 출구, 수쿰윗 병원 맞은편', phone: '095-868-6826' },
    th: { name: 'สาขาเอกมัย', address: 'BTS เอกมัย ทางออก 4 ตรงข้ามโรงพยาบาลสุขุมวิท', phone: '095-868-6826' },
    en: { name: 'Ekamai Branch', address: 'BTS Ekamai Exit 4, opposite Sukhumvit Hospital', phone: '095-868-6826' }
  },
  'Phrom Phong': {
    ko: { name: '프롬퐁 지점', address: '수쿰윗 소이 24, 아리스톤 호텔 1층', phone: '02-115-5500' },
    th: { name: 'สาขาพร้อมพงษ์', address: 'สุขุมวิท ซอย 24 ชั้น 1 โรงแรมอริสตัน', phone: '02-115-5500' },
    en: { name: 'Phrom Phong Branch', address: 'Sukhumvit Soi 24, 1st Floor Ariston Hotel', phone: '02-115-5500' }
  }
};

// 공간 정보
const spaceInfo = {
  ko: spaceType === 'Room' ? `룸 ${roomNumber}` : `홀 ${floor}`,
  th: spaceType === 'Room' ? `ห้อง${roomNumber.replace('Room ', '')}` : `ชั้น${floor.replace('st Floor', '').replace('nd Floor', '')}`,
  en: spaceType === 'Room' ? `${roomNumber}` : `${floor}`
};

// 구글맵 링크
const googleMapsLinks = {
  'Ekamai': 'https://maps.google.com/?q=Ekamai+BTS+Station+Exit+4',
  'Phrom Phong': 'https://maps.google.com/?q=Sukhumvit+Soi+24+Ariston+Hotel'
};

return [{
  json: {
    action: actionData.action,
    bookingNumber,
    name,
    email,
    language: langCode,
    dateFormatted: dateFormatted[langCode],
    timeFormatted,
    branch,
    branchInfo: branchInfo[branch][langCode],
    spaceInfo: spaceInfo[langCode],
    guests,
    specialRequest,
    googleMapsLink: googleMapsLinks[branch]
  }
}];

// 확정 이메일 템플릿 생성 (기존 워크플로우 2와 동일)
